        private void ExitEndorsement( int OppEndoID)
        {
            RemoveSessionLookUp();

            if (Session["AUTOEndorsement"] != null)
            {
                 //Para que vuelva a la poliza original.
                Login.Login cp = HttpContext.Current.User as Login.Login;
                int UserID = int.Parse(cp.Identity.Name.Split("|".ToCharArray())[1]);

                EPolicy.TaskControl.AutoHighLimit OpptaskControl = (EPolicy.TaskControl.AutoHighLimit)Session["AUTOEndorsement"];
                int taskControlIDPolicy = OpptaskControl.TaskControlID;
                Session.Remove("AUTOEndorsement");
                Session.Clear();

                EPolicy.TaskControl.AutoHighLimit QA = (EPolicy.TaskControl.AutoHighLimit)TaskControl.TaskControl.GetTaskControlByTaskControlID(taskControlIDPolicy, UserID);
                QA.Mode = (int)EPolicy.TaskControl.TaskControl.TaskControlMode.CLEAR;
                QA.IsEndorsement = false;
                Session["TaskControl"] = QA;
                Response.Redirect("AutoHighLimit.aspx?EndorsementSection=" + OppEndoID.ToString(), false);
            }
            else
            {
                Session.Clear();
                Response.Redirect("Search.aspx");
            }
        }

