            {
                ddlAgent.Width = 400;
            }
            else
            {
                ddlAgent.Width = 300;
            }

            double GrandTotalValue = 0, TotalQuote = 0;

            double.TryParse(txtTotalQuote.Text,
               System.Globalization.NumberStyles.Number | System.Globalization.NumberStyles.AllowCurrencySymbol,
