               System.Globalization.NumberStyles.Number | System.Globalization.NumberStyles.AllowCurrencySymbol,
               System.Globalization.CultureInfo.CreateSpecificCulture("en-US"),
               out GrandTotalValue);

            if (TotalQuote > 0)
            {
                txtGrossTax.Text =
                    TotalQuote > 0 ?
                    (Math.Round(CalculateAgentCommision(ddlAgent.SelectedItem.Value, TotalQuote), 2)).ToString("###,###.00")
                    : "0";
            }
            else
            {
                txtGrossTax.Text =
                    GrandTotalValue > 0 ?
                    (Math.Round(CalculateAgentCommision(ddlAgent.SelectedItem.Value, GrandTotalValue), 2)).ToString("###,###.00")
                    : "0";
            }

            btnCalculate_Click(String.Empty, EventArgs.Empty);
        }

