            DbRequestXmlCooker.AttachCookItem("DriverMaritalStatus", SqlDbType.Int, 0, ddlDriverMaritalStatus.SelectedValue.Trim(), ref cookItems);

            XmlDocument xmlDoc;

            try
            {
                xmlDoc = DbRequestXmlCooker.Cook(cookItems);
            }
            catch (Exception ex)
