                throw new Exception("Could not cook items.", ex);
            }

            Baldrich.DBRequest.DBRequest exec = new Baldrich.DBRequest.DBRequest();
            DataTable dtDrivers = exec.GetQuery("GetDrivers", xmlDoc);

            return dtDrivers;
