                //txtSurcharge.Text = Math.Round(((GrandTotal - double.Parse(txtTotalDiscount.Text.ToString())) * (taskControl.SurchargePct / 100)), 0).ToString();
                txtSurcharge.Text = Math.Round(((GrandTotal) * (taskControl.SurchargePct / 100)),0).ToString();
            }

            if (double.Parse(txtSurcharge.Text.ToString()) != 0.0)
            {
                //Apply Surcharge
                txtGrandTotal.Text = (GrandTotal + double.Parse(txtSurcharge.Text.ToString())).ToString();
