                EPolicy.TaskControl.AutoHighLimit taskControl = (EPolicy.TaskControl.AutoHighLimit)Session["TaskControl"];

                for (int i = 0; i < GridInsured.Columns.Count - 1; i++)
                {
                    GridInsured.Columns[i].Visible = true;
                }

                switch (e.CommandName)
                {
                    case "Select": //Edit
                        int index = Int32.Parse(e.CommandArgument.ToString());
                        GridViewRow row = GridInsured.Rows[index];
                        GridInsured.BorderColor = Color.Red;



                        if (row.Cells[3].Text.Trim() == TxtFirstName.Text.Trim().ToUpper() && row.Cells[4].Text.Trim() == TxtLastName.Text.Trim().ToUpper() && row.Cells[8].Text.Trim() == TxtBirthDate.Text)
                        {

                            GridInsured.BorderColor = Color.Black;
                            ShowMessageDialog("Selected Insured is the primary Insured, to edit go to \"Client Information\" section!");
                            GridInsured.Columns[9].Visible = false; //trafic violation
                            GridInsured.Columns[10].Visible = false; //trafic surcharge
                            GridInsured.Columns[11].Visible = true; //UnderageSurcharge
                            GridInsured.Columns[12].Visible = true;// Sumofcharges
                            GridInsured.Columns[13].Visible = true;//Gradepointavg
                            GridInsured.Columns[14].Visible = true;//delete
                            GridInsured.Columns[15].Visible = false;//TaskControl
                            GridInsured.Columns[2].Visible = false;

                            if (lblModifyInsured.Visible == true && TxtInsuredName.Text != "")
                            {
                                BtnCancelInsured_Click("", e);
                                //TxtInsuredName.Text = "";
                                //TxtInsuredLastName.Text = "";
                            }

                            return;

                        }

                        this.BtnAddInsured.Text = "Save Insured";
                        lblModifyInsured.Visible = true;



                        TxtInsuredName.Text = row.Cells[3].Text.Trim(); //GridInsured.SelectedRow.Cells[1].Text.Trim();
                        TxtInsuredLastName.Text = row.Cells[4].Text.Trim(); //GridInsured.SelectedRow.Cells[1].Text.Trim();
                        ddlInsuredGender.SelectedIndex = ddlInsuredGender.Items.IndexOf(ddlInsuredGender.Items.FindByText(row.Cells[5].Text.Trim()));
                        ddlInsuredMaritalStatus.SelectedIndex = ddlInsuredMaritalStatus.Items.IndexOf(ddlInsuredMaritalStatus.Items.FindByText(row.Cells[6].Text.Trim()));

                        TxtInsuredAge.Text = row.Cells[7].Text.Trim(); //GridInsured.SelectedRow.Cells[4].Text.Trim();
                        TxtInsuredBirthDate.Text = row.Cells[8].Text.Trim(); //GridInsured.SelectedRow.Cells[5].Text.Trim();
                        txtViolationPoints.Text = row.Cells[9].Text.Trim();
                        txtViolationSurchargePct.Text = row.Cells[10].Text.Trim();
                        txtUnderageChargesPct.Text = row.Cells[11].Text.Trim().Replace("&nbsp;", "");
                        TxtInsuredGrade.Text = row.Cells[13].Text.Trim();

                        if (TxtInsuredAge.Text == "17" || TxtInsuredAge.Text == "16")
                        {
                            TxtInsuredGrade.Visible = true;
                        }

                        if (txtViolationPoints.Text == "")
                            txtViolationPoints.Text = "0";

                        if (txtViolationSurchargePct.Text == "")
                            txtViolationSurchargePct.Text = "0";

                        if (txtUnderageChargesPct.Text == "")
                            txtUnderageChargesPct.Text = "0";

                        //TxtInsuredID.Text = GridInsured.SelectedIndex.ToString();
                        //TxtInsuredID.Text = row.

                        DataTable dt = taskControl.DriversCollection.Copy();
                        dt = AddRemoveAdditionalInsured(dt, false);
                        TxtInsuredID.Text = dt.Rows[index]["DriverDetailID"].ToString();
                        //TxtInsuredID.Text = row.Cells[2].Text.Trim(); //GridInsured.SelectedRow.Cells[7].Text;


                        GridInsured.Columns[9].Visible = false; //trafic violation
                        GridInsured.Columns[10].Visible = false; //trafic surcharge
                        GridInsured.Columns[11].Visible = true; //UnderageSurcharge
                        GridInsured.Columns[12].Visible = true;// Sumofcharges
                        GridInsured.Columns[13].Visible = true;//Gradepointavg
                        GridInsured.Columns[14].Visible = false;//delete
                        GridInsured.Columns[15].Visible = false;//TaskControl
                        GridInsured.Columns[2].Visible = false;


                        break;
                    case "Delete":

                        if (lblModifyInsured.Visible == true)
                        {
                            throw new Exception("Please save or cancel selected Insured first.");
                        }
                        else
                        {
                            int indexd = Int32.Parse(e.CommandArgument.ToString());
                            if (GridInsured.SelectedIndex == -1)
                            {
                                GridInsured.SelectedIndex = 0;
                            }

                            DataTable dts = taskControl.DriversCollection.Copy();
                            dts = AddRemoveAdditionalInsured(dts, false);
                            if (dts.Rows.Count > 0)
                            {
                                string DetailID = dts.Rows[indexd]["DriverDetailID"].ToString();
                                DataRow[] rows = taskControl.DriversCollection.Select("DriverDetailID = '" + DetailID + "'");
                                if (rows.Count() > 0)
                                {
                                    indexd = taskControl.DriversCollection.Rows.IndexOf(rows[0]);
                                }
                            }

                            DeleteInsuredDetail(indexd);
                            //DeleteInsuredDetail(e.CommandName.IndexOf("InsuredDetailID"));

                            GridInsured.Columns[9].Visible = false; //trafic violation
                            GridInsured.Columns[10].Visible = false; //trafic surcharge
                            GridInsured.Columns[11].Visible = true; //UnderageSurcharge
                            GridInsured.Columns[12].Visible = true;// Sumofcharges
                            GridInsured.Columns[13].Visible = true;//Gradepointavg
                            GridInsured.Columns[14].Visible = true;//delete
                            GridInsured.Columns[15].Visible = false;//TaskControl
                            GridInsured.Columns[2].Visible = false;
                        }
                        break;
                    default: //Page
                        break;
                }

                //GridInsured.Columns[9].Visible = false; //trafic violation
                //GridInsured.Columns[10].Visible = false; //trafic surcharge
                //GridInsured.Columns[11].Visible = true; //UnderageSurcharge
                //GridInsured.Columns[12].Visible = true;// Sumofcharges
                //GridInsured.Columns[13].Visible = true;//Gradepointavg
                //GridInsured.Columns[14].Visible = true;//delete
                //GridInsured.Columns[15].Visible = false;//TaskControl
                //GridInsured.Columns[2].Visible = false;

            }
            catch (Exception exp)
            {
                ShowMessageDialog(exp.Message);
            }
        }

