        private void DeleteInsuredDetail(int index)
        {
            try
            {
                EPolicy.TaskControl.AutoHighLimit taskControl = (EPolicy.TaskControl.AutoHighLimit)Session["TaskControl"];

                DataTable dt = null;

                taskControl.DriversCollection.Rows.RemoveAt(index);
                taskControl.DriversCollection.AcceptChanges();
                dt = taskControl.DriversCollection;

                FillInsuredDetailGrid();
            }
            catch (Exception xcp)
            {
                throw new Exception("Error deleting row.", xcp);
            }
        }

        private void FillInsuredDetailGrid()
        {
            try
            {
                EPolicy.TaskControl.AutoHighLimit taskControl = (EPolicy.TaskControl.AutoHighLimit)Session["TaskControl"];


                GridInsured.DataSource = null;
                DataTable dt = null;

                dt = taskControl.DriversCollection.Copy();

                for (int i = 0; i < GridInsured.Columns.Count - 1; i++)
                {
                    GridInsured.Columns[i].Visible = true;
                }

                if (dt != null)
                {
                    if (dt.Rows.Count != 0)
                    {
                        //for (int i = 0; i < dt.Rows.Count; i++)
                        //{
                        //    if ((bool)dt.Rows[i]["IsAdditionalInsured"] == false)
                        //    {
                        //        dt.Rows.RemoveAt(i);
                        //    }
                        //}
                        dt = AddRemoveAdditionalInsured(dt, false);

                        GridInsured.DataSource = dt;
                        GridInsured.DataBind();
                    }
                    else
                    {
                        GridInsured.DataSource = null;
                        GridInsured.DataBind();
                    }
                }
                else
                {
                    GridInsured.DataSource = null;
                    GridInsured.DataBind();
                }

                GridInsured.Columns[9].Visible = false; //trafic violation
                GridInsured.Columns[10].Visible = false; //trafic surcharge
                GridInsured.Columns[11].Visible = true; //UnderageSurcharge
                GridInsured.Columns[12].Visible = true;// Sumofcharges
                GridInsured.Columns[13].Visible = true;//Gradepointavg
                GridInsured.Columns[14].Visible = true;//delete
                GridInsured.Columns[15].Visible = false;//TaskControl
                GridInsured.Columns[2].Visible = false;

            }
            catch (Exception exp)
            {
                ShowMessageDialog(exp.Message);
            }
        }

        protected void TxtInsuredBirthDate_TextChanged(object sender, EventArgs e)
        {
            try
            {
                if (TxtInsuredBirthDate.Text != "")
                {
                    TimeSpan ts = DateTime.Parse(DateTime.Now.ToShortDateString()) - DateTime.Parse(this.TxtInsuredBirthDate.Text);

                    // Difference in days.
                    double totdays = int.Parse(ts.Days.ToString());
                    double tot = 0;

                    if (totdays < 0)
                    {
                        totdays = 0;
                    }
                    else
                    {
                        tot = Math.Floor(totdays / 365);
                        TxtInsuredAge.Text = tot.ToString().Trim();
                    }
                    if (TxtInsuredAge.Text == "16" || TxtInsuredAge.Text == "17")
                    {
                        lblInsuredGrade.Visible = true;
                        TxtInsuredGrade.Visible = true;
                        TxtInsuredGrade.Text = "0";
                    }
                    else if (TxtInsuredGrade.Text != "16" || TxtInsuredGrade.Text != "17")
                    {
                        lblInsuredGrade.Visible = false;
                        TxtInsuredGrade.Visible = false;
                    }

                    //GetSurchargeAge("BirthDate");
                    CalculatePremium();
                    //TxtInsuredAge.Focus();
                }
            }
            catch (Exception ecp)
            {
                ShowMessageDialog("Could not calculate Driver's Age.");
            }
        }

