        private DataTable GetOver2TonIsHeavyByGetOver2TonIsHeavyID(int Over2TonIsHeavyID)
        {
            DataTable dt1 = null;

            DbRequestXmlCookRequestItem[] cookItems = new DbRequestXmlCookRequestItem[1];
            DbRequestXmlCooker.AttachCookItem("Over2TonIsHeavyID", SqlDbType.Int, 0, Over2TonIsHeavyID.ToString(), ref cookItems);
            
            DBRequest exec = new DBRequest();
            XmlDocument xmlDoc;

            try
            {
                xmlDoc = DbRequestXmlCooker.Cook(cookItems);
            }
            catch (Exception ex)
            {
                throw new Exception("Could not cook items.", ex);
            }

            try
            {
                dt1 = exec.GetQuery("GetOver2TonIsHeavyByOver2TonIsHeavyID", xmlDoc);
            }
            catch (Exception ex)
            {
                throw new Exception("Could not retrieve the Over2Ton Types.", ex);
            }

            return dt1;
        }
		
