        protected bool CalculateRates(ref string BI, ref string PD, ref string MP, ref string Coll, ref string Comp, ref string Mot, ref string Add, ref string RentReim, ref string TaxiLoss, ref string VIPA, bool Calc_MP)
        {
           bool rtValue = true;
           DataTable dt = null;
           DataTable dtPhsDam = null;

           if (ddlDiscountPhys.SelectedItem.Text.Trim() == "")
                 if (ddlDiscountPhys.Items.FindByValue("0") != null)
                                ddlDiscountPhys.SelectedValue = "0";

           if (ddlDiscountLiab.SelectedItem.Text.Trim() == "")
               if (ddlDiscountLiab.Items.FindByValue("0") != null)
                   ddlDiscountLiab.SelectedValue = "0";

            try
            {
                if (ValidateBI())
                {
                    dt = GetLiabilityRates(ddlVehicleUse.SelectedItem.Value.ToString(), ddlIsland.SelectedItem.Value.ToString(), radGenTons.SelectedItem.Value.ToString(), radMotorcycle.SelectedItem.Value.ToString(), int.Parse(TxtPassengerNo.Text.ToString()), radHeavyTruck.SelectedItem.Value.ToString());

                    if (dt.Rows.Count > 0)
                    {

                        if (Calc_MP)
                        {
                            MP = dt.Rows[0]["MPRate"].ToString();
                        }
                        else
                        {
