            {
                if (RenewalPremium != "")
                {
                    //UpdateNetTotalPremiumRenewal(taskControl.TaskControlID, float.Parse(RenewalPremium));
                }
            }

            if (taskControl.TaskControlID != 0 && taskControl.TaskControlID.ToString() != "")
            {
                DataTable dt = null;

                dt = EPolicy.TaskControl.AutoHighLimit.GetNetTotalPremiumByTaskControlID(taskControl.TaskControlID);
                if (dt.Rows.Count > 0)
                {
                    //When modifing Verifies premium claculated, if the premium is not equal or does not have 1 dollar less or 1 dollar more the total premium shwon will be the one calculated inside the application
                    if(double.Parse(dt.Rows[0]["XML_NetPremium"].ToString()).ToString("##,###.00") != "0")
                    {
                        txtTotalQuote.Text = double.Parse(dt.Rows[0]["XML_NetPremium"].ToString()).ToString("##,###.00");

                        double GrossTax = 0, TotalQuote = 0;

                        double.TryParse(txtTotalQuote.Text,
                           System.Globalization.NumberStyles.Number | System.Globalization.NumberStyles.AllowCurrencySymbol,
                           System.Globalization.CultureInfo.CreateSpecificCulture("en-US"),
                           out TotalQuote);

                        txtGrossTax.Text =
                            TotalQuote > 0.0 ?
                            (Math.Round(CalculateAgentCommision(ddlAgent.SelectedItem.Value, TotalQuote), 2)).ToString("###,###.00")
                            : "0";

                        double.TryParse(txtGrossTax.Text,
                            System.Globalization.NumberStyles.Number | System.Globalization.NumberStyles.AllowCurrencySymbol,
                            System.Globalization.CultureInfo.CreateSpecificCulture("en-US"),
                            out GrossTax);

                        if (taskControl.isQuote)
                            txtTotalQuote.Text = GrossTax > 0 ? (TotalQuote + GrossTax).ToString("##,###.00") : txtTotalQuote.Text;
                    }
                    else
                    {
                        txtTotalQuote.Text = double.Parse(txtGrandTotal.Text).ToString("##,###.00");
                    }
                }
                else
                {
                    txtTotalQuote.Text = double.Parse(txtGrandTotal.Text).ToString("##,###.00");
                }
            }
            else
            {
                txtTotalQuote.Text = double.Parse(txtGrandTotal.Text).ToString("##,###.00");
            }


        }

        private void GenerateQRCode(ref string Values)
        {
            EncryptClass.EncryptClass encryptClass = new EncryptClass.EncryptClass();
            Values = encryptClass.Encrypt(Values);
            string FileNameLogo = "QR" + Values.ToString() + ".png";
             //Values = "https://epps.guardianinsurance.com/ValidInfoQrCode.aspx?Tc=" + Values;
             Values = "https://epps-test.guardianinsurance.com/ValidInfoQrCode.aspx?Tc=" + Values;

             //Values = "https://epps-test.guardianinsurance.com/ValidInfoQrCode.aspx?Tc=" + Values+"&isPolicy=y";
             
    
            string imgPath = System.Web.Hosting.HostingEnvironment.MapPath("~/QRCodes/");

            var url = string.Format("http://chart.apis.google.com/chart?cht=qr&chs={1}x{2}&chl={0}", Values, "500", "500");
            WebResponse response = default(WebResponse);
            Stream remoteStream = default(Stream);
            StreamReader readStream = default(StreamReader);
            WebRequest request = WebRequest.Create(url);
            response = request.GetResponse();
            remoteStream = response.GetResponseStream();
            readStream = new StreamReader(remoteStream);
            System.Drawing.Image img = System.Drawing.Image.FromStream(remoteStream);
            CleanillegalChar(ref FileNameLogo);
            img.Save(imgPath + FileNameLogo);
            Values = imgPath + FileNameLogo;
            response.Close();
            remoteStream.Close();
            readStream.Close();
        }

        private string CleanillegalChar(ref string messyText)
        {
            Regex regex = new Regex(@"[\s,:;/\\|]+");
            messyText = regex.Replace(messyText, "").Replace("\"", "");
            return messyText;
        }


