                                            renewalcollection.Rows[i]["CollPremium"] = (Math.Round(double.Parse(renewalcollection.Rows[i]["CollPremium"].ToString()) * Surcharge, 0)).ToString();
                                            renewalcollection.Rows[i]["BIPremium"] = (Math.Round(double.Parse(renewalcollection.Rows[i]["BIPremium"].ToString()) * Surcharge, 0)).ToString();
                                            renewalcollection.Rows[i]["PDPremium"] = (Math.Round(double.Parse(renewalcollection.Rows[i]["PDPremium"].ToString()) * Surcharge, 0)).ToString();
                                            renewalcollection.Rows[i]["MPPremium"] = (Math.Round(double.Parse(renewalcollection.Rows[i]["MPPremium"].ToString()) * Surcharge, 0)).ToString();
                                            renewalcollection.AcceptChanges();
                                        }

                                        taskControl.VehicleCollection = renewalcollection.Copy();
                                        taskControl.CustomerCollection = "1";
                                        Session["TaskControl"] = taskControl;
                                        CalculateRenewalPremium(true);
                                        PPSTotalPremium = int.TryParse(txtGrandTotal.Text.ToString(), out PPSTotalPremium) ? int.Parse(txtGrandTotal.Text.ToString()) : 0;
                                    }
                                }
                            }

                            //3/18/2020 Peticion por Ramon Se elimino la comparacion de Prima con PPS
                            if (EffectiveDate >= DateTime.Parse("03/01/2019") && EffectiveDate <= DateTime.Parse("05/31/2020"))
                            {
                                if (PPSTotalPremium < EPPSTotalPremium)
                                {
                                    taskControl.TotalDiscounts = (double.Parse(EPPSTotalPremium.ToString()) - double.Parse(PPSTotalPremium.ToString()));
                                    taskControl.TotalDiscountsPct = Math.Round((Math.Round((((double.Parse(PPSTotalPremium.ToString()) / double.Parse(EPPSTotalPremium.ToString())) - 1.0) * (-1.0)), 2)) * (100), 0);
                                    taskControl.VehicleCollection = renewalcollection.Copy();
                                    taskControl.CustomerCollection = "1";
                                }
                                else
                                {
                                    taskControl.VehicleCollection = eppscollection.Copy();
                                    taskControl.CustomerCollection = "0";
                                }
                            }
                            else
                            {
                                taskControl.VehicleCollection = eppscollection.Copy();
                                taskControl.CustomerCollection = "0";
                            }

                            CalculateRenewalPremium(true);
                            GetTotalPremium();
                            Session["EppsCollection"] = null;
                            Session["TaskControl"] = taskControl;
                        }
                        else if (Liability)
                        {
                            double Underagesurcharge = 0;
                            int MostMinor = 0;
                            GetMostMinor(taskControl, ref MostMinor, ref Underagesurcharge);
                            /// IF the policy have minors eliminate underage discount
                            if (MostMinor <= 25)
                            {
                                for (int J = 0; J < taskControl.VehicleCollection.Rows.Count; J++)
                                {
                                    if (double.Parse(taskControl.VehicleCollection.Rows[J]["UnderAgeSurcharge"].ToString().Trim()) != 0.0)
                                    {
                                        double UnderAgeValue = double.Parse(taskControl.VehicleCollection.Rows[J]["UnderAgeSurcharge"].ToString().Trim());
                                        taskControl.VehicleCollection.Rows[J]["TotalPremium"] = double.Parse(taskControl.VehicleCollection.Rows[J]["TotalPremium"].ToString().Trim()) - double.Parse(taskControl.VehicleCollection.Rows[J]["UnderAgeSurcharge"].ToString().Trim());
                                        taskControl.VehicleCollection.Rows[J]["UnderAgeSurcharge"] = "0.0";
                                        taskControl.VehicleCollection.AcceptChanges();
                                        if (txtTotalCharges.Text.ToString().Trim() != "")
                                            txtTotalCharges.Text = (double.Parse(txtTotalCharges.Text.ToString().Trim()) - UnderAgeValue).ToString();
                                    }
                                }
                                //3/18/2020 Peticion por Ramon Se elimino la comparacion de Prima con PPS
                                DateTime EffectiveDate = GetRenewalEffectiveDate();
                                if (EffectiveDate >= DateTime.Parse("03/01/2019") && EffectiveDate <= DateTime.Parse("05/31/2020"))
                                    taskControl.CustomerCollection = "1";
                                else
                                    taskControl.CustomerCollection = "0";

                                Session["EppsCollection"] = null;
                                Session["TaskControl"] = taskControl;
                            }
                            else
                            {
                                //3/18/2020 Peticion por Ramon Se elimino la comparacion de Prima con PPS
                                DateTime EffectiveDate = GetRenewalEffectiveDate();
                                if (EffectiveDate >= DateTime.Parse("03/01/2019") && EffectiveDate <= DateTime.Parse("05/31/2020"))
                                    taskControl.CustomerCollection = "1";
                                else
                                    taskControl.CustomerCollection = "0";

                                Session["EppsCollection"] = null;
                                Session["TaskControl"] = taskControl;
                            }
                        }
                        else
                        {
                            taskControl.VehicleCollection = renewalcollection.Copy();

                            //3/18/2020 Peticion por Ramon Se elimino la comparacion de Prima con PPS
                            DateTime EffectiveDate = GetRenewalEffectiveDate();
                            if (EffectiveDate >= DateTime.Parse("03/01/2019") && EffectiveDate <= DateTime.Parse("05/31/2020"))
