                                    renewalcollection.Rows[i]["CompPremium"] = NetTotalPremiumdt.Rows[i]["NetCompPremium"];
                                    renewalcollection.Rows[i]["CollPremium"] = NetTotalPremiumdt.Rows[i]["NetCollPremium"];
