                                    //taskControl.VehicleCollection.Rows[y]["TotalPremium"] = double.Parse(taskControl.VehicleCollection.Rows[y]["TotalPremium"].ToString()) + UnderAgeCharge + ViolationCharge;

                                    if (OnTheFly)
