        private DataTable DiscountEffectiveDate(DataTable dtDiscount)
        {
            DateTime EffectiveDate = GetRenewalEffectiveDate();

            if (dtDiscount.Rows.Count > 0)
                if (DateTime.Parse(EffectiveDate.ToShortDateString()) < DateTime.Parse("03/01/2019"))
                    if (dtDiscount.Rows[0]["Renewal"].ToString() == "False")
                        dtDiscount.Rows[0]["Discount"] = "35";
                    else
                        dtDiscount.Rows[0]["Discount"] = "15";
            return dtDiscount;
        }

        private DateTime GetRenewalEffectiveDate()
        {
            EPolicy.TaskControl.AutoHighLimit taskControl = (EPolicy.TaskControl.AutoHighLimit)Session["TaskControl"];

            if (taskControl.RenewalNo != "")
                GetPolicyFromPPS(taskControl.RenewalNo, false);
            else
                return new DateTime();

            DateTime EffectiveDate = new DateTime();
            XmlDocument XmlDoc = new XmlDocument();
            XmlDoc.LoadXml(Current_XML);

            XmlNodeList XmlBase = XmlDoc.GetElementsByTagName("Policy");

            foreach (XmlNode XmlPolicyBase in XmlBase)
            {
                EffectiveDate = DateTime.Parse(DateTime.Parse(XmlPolicyBase["Incept"].InnerText).ToShortDateString()).AddYears(1);
            }

            EffectiveDate = DateTime.Parse(EffectiveDate.ToShortDateString()) < DateTime.Parse(DateTime.Today.ToShortDateString()) ? DateTime.Parse(DateTime.Today.ToShortDateString()) : DateTime.Parse(EffectiveDate.ToShortDateString());

            return EffectiveDate;
        }

