        private static StringBuilder GetAddedorRemovedInsured(EPolicy.TaskControl.AutoHighLimit[] Autos, StringBuilder sb)
        {
            List<DataRow> topLineInsuredCollectionRows = new List<DataRow>();
            List<DataRow> bottomLineInsuredCollectionRows = new List<DataRow>();
            Dictionary<string, string>[] tempContainer; // Hack because Dictionary requires unique keys
            List<Dictionary<string, string>> insuredInQuoteTopRow = new List<Dictionary<string, string>>();
            List<Dictionary<string, string>> insuredInQuoteBottomRow = new List<Dictionary<string, string>>();

            foreach (DataRow row in Autos[0].DriversCollection.Rows)
                topLineInsuredCollectionRows.Add(row);

            foreach (DataRow row in Autos[1].DriversCollection.Rows)
                bottomLineInsuredCollectionRows.Add(row);

            for (int i = 0; i < topLineInsuredCollectionRows.Count; i++)
            {
                tempContainer = new Dictionary<string, string>[] { new Dictionary<string, string>() };

                for (int j = 0; j < topLineInsuredCollectionRows[i].Table.Columns.Count; j++)
                {
                    tempContainer[0].Add(
                        topLineInsuredCollectionRows[i].Table.Columns[j].ColumnName,
                        topLineInsuredCollectionRows[i].ItemArray[j].ToString()
                    );
                }
                insuredInQuoteTopRow.Add(tempContainer[0]);
            }

            for (int i = 0; i < bottomLineInsuredCollectionRows.Count; i++)
            {
                tempContainer = new Dictionary<string, string>[] { new Dictionary<string, string>() };

