		 private string[] GetVINDecode(string VIN)
        {
            try
            {
                string[] vehicleInfo = new String[5];
                lblVINResult.Text = "";
                //String url = "https://vpic.nhtsa.dot.gov/api/vehicles/DecodeVIN/";
                String url = "https://vpic.nhtsa.dot.gov/api/vehicles/decodevin/" + VIN + "?format=xml";
                HttpWebRequest request = WebRequest.Create(url) as HttpWebRequest;
                request.Method = "get";
                request.ContentType = "application/x-www-form-urlencoded";

                UTF8Encoding encoding = new UTF8Encoding();

                string VinXmlResponse = "";
                StreamReader reader;
                using (HttpWebResponse response = request.GetResponse() as HttpWebResponse)
                {
                    reader = new StreamReader(response.GetResponseStream());
                    var result = reader.ReadToEnd();
                    VinXmlResponse = result;
                }

                  string filenameForVinDecode = System.Configuration.ConfigurationManager.AppSettings["FileNameForVinDecode"].ToString() + "Vindecode" + 
                DateTime.Now.Month.ToString() + DateTime.Now.Day.ToString() + DateTime.Now.Year.ToString() + DateTime.Now.Hour.ToString() + DateTime.Now.Minute.ToString()+ DateTime.Now.Second.ToString() + ".xml";
             
                File.WriteAllText(filenameForVinDecode, VinXmlResponse, Encoding.ASCII);
                XmlDocument xmldoc = new XmlDocument();
                XmlNodeList xmlnode;
                XmlNodeList xmlnodeERROR;
                int i = 0;
                string str = null;
                FileStream fs = new FileStream(filenameForVinDecode, FileMode.Open, FileAccess.Read);
                xmldoc.Load(fs);

                //eRROR

                xmlnodeERROR = xmldoc.GetElementsByTagName("Message");
                for (i = 0; i <= xmlnodeERROR.Count - 1; i++)
                {
                    xmlnodeERROR[i].ChildNodes.Item(0).InnerText.Trim();
                    str = xmlnodeERROR[i].ChildNodes.Item(0).InnerText.Trim();

                    if (!str.Contains("Results returned successfully"))
                    {
                        lblVINResult.Text = str;
                    }
                }


                bool varError = false;
                xmlnode = xmldoc.GetElementsByTagName("DecodedVariable");
                string makeid = "";
                string modelid = "";
                string year = "";
                //xmlnode = xmldoc.GetElementsByTagName("Results");
                for (i = 0; i <= xmlnode.Count - 1; i++)
                {
                    /* if (xmlnode[i].ChildNodes.Item(1).InnerText.Trim() == "Error Text")
                    {
                        if (!xmlnode[i].ChildNodes.Item(3).InnerText.Trim().Contains("VIN decoded clean"))
                        {
                            varError = true;
                            str = xmlnode[i].ChildNodes.Item(2).InnerText.Trim() + " - " + xmlnode[i].ChildNodes.Item(3).InnerText.Trim();
                            lblVINResult.Text = str;

                            vehicleInfo[4] = xmlnode[i].ChildNodes.Item(3).InnerText.Trim();
                        }
                    } */

                    // DivAlert += @"<p><b>.	Insured ID </b></p>";
                    if (!varError)
                    {
                        if (xmlnode[i].ChildNodes.Item(1).InnerText.Trim() == "Make")
						{
							if (xmlnode[i].ChildNodes.Count > 3)
							{
								lblVINResult.Text = "";
								makeid = xmlnode[i].ChildNodes.Item(2).InnerText.Trim();
								lblVINResult.Text = "Please be sure to select the correct make, model and year for the vin number entered. " + @" <b>" + lblVINResult.Text + xmlnode[i].ChildNodes.Item(3).InnerText.Trim().ToUpper() + "</b>";
							}
						}

						if (xmlnode[i].ChildNodes.Item(1).InnerText.Trim() == "Model")
						{
							if (xmlnode[i].ChildNodes.Count > 3)
							{
								modelid = xmlnode[i].ChildNodes.Item(2).InnerText.Trim();
								lblVINResult.Text = lblVINResult.Text + @" <b>" + xmlnode[i].ChildNodes.Item(3).InnerText.Trim().ToUpper() + "</b>";
							}
						}

						if (xmlnode[i].ChildNodes.Item(1).InnerText.Trim() == "Model Year")
						{
							if (xmlnode[i].ChildNodes.Count > 3)
							{

								year = xmlnode[i].ChildNodes.Item(3).InnerText.Trim();
								lblVINResult.Text = " " + lblVINResult.Text + @" <b>" + xmlnode[i].ChildNodes.Item(3).InnerText.Trim() + "</b>";
							}
						}

                        vehicleInfo[0] = makeid;
                        vehicleInfo[1] = modelid;
                        vehicleInfo[2] = year;


                        ////SetDDLMake
                        //ddlMake.SelectedIndex = ddlMake.Items.IndexOf(ddlMake.Items.FindByValue(makeid));
                        //FillModelByMakeID(makeid);
                        ////SetDDLMake
                        //ddlModel.SelectedIndex = ddlModel.Items.IndexOf(ddlModel.Items.FindByValue(modelid));
                    }
                }

                reader.Close();

                fs.Flush();
                fs.Close();

                return vehicleInfo;
            }
            catch (Exception ex)
            {
                throw new Exception(ex.Message);
            }
        }
